    <!-- Button to trigger random selection -->
    <button id="randomButton">Show Random Item</button>
    <!-- Container for displaying data -->
    <div id="data"></div>
</main>
````

### /workspaces/ISYS3004_STAHURA_19174279_Asse1/js/APIscript.js

