````javascript
// filepath: /workspaces/ISYS3004_STAHURA_19174279_Asse1/js/APIscript.js
let fetchedData = []; // Variable to store fetched data

fetch('https://jsonplaceholder.typicode.com/posts')
  .then(response => response.json())
  .then(data => {
    fetchedData = data; // Store the fetched data
    console.log(data); // Log the data for inspection
    const dataDiv = document.getElementById('data');
    if (data.length > 0) {
      dataDiv.innerHTML = '<h2>' + data[0].title + '</h2>'; // Display the first item initially
    } else {
      dataDiv.innerHTML = '<p>No data found.</p>';
    }
  })
  .catch(error => {
    console.error('Error fetching data:', error);
    document.getElementById('data').innerHTML = '<p>Sorry, an error occurred while fetching data.</p>';
  });

// Add event listener to the button for random selection
document.getElementById('randomButton').addEventListener('click', () => {
  if (fetchedData.length > 0) {
    const randomIndex = Math.floor(Math.random() * fetchedData.length); // Generate a random index
    const randomItem = fetchedData[randomIndex]; // Get the random item
    document.getElementById('data').innerHTML = '<h2>' + randomItem.title + '</h2>'; // Display the random item
  } else {
    document.getElementById('data').innerHTML = '<p>No data available to display.</p>';
  }
});
````

